import React, { useState } from "react";
import { Container, Grid, Divider, Form, Button } from "semantic-ui-react";
import { useHistory } from "react-router-dom";
import { Link } from "react-router-dom";
import "./App.css";

const Login = ( { showRegisterLink }) => {
    const history = useHistory();

    const [usernamePassword, setUsernamePassword] = useState ({
        username: "",
        password: ""
    });

    const [usernamePasswordError, setUsernamePasswordError ] = useState ({
        username: null,
        password: null,
    });

    const handleChange = (e) => {
        const {currentTarget} = e;
        const{value, name} = currentTarget;
        setUsernamePassword({
            ...usernamePassword, [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const { username, password } = usernamePassword;
        if( username.length < 5 || usernamePassword.length > 255 ){
            setUsernamePasswordError({
                ...usernamePasswordError,
                username: "Lutfen kullanici adinizi kontrol edin",
            });
            return;
        }else {
            setUsernamePasswordError({
                username:null,
            });
        }
        if ( password.length < 3 || password.length > 255 ){
            setUsernamePasswordError({
                ...usernamePasswordError,
                password: "Lutfen sifrenizi kontrol edin",
            });
            return;
        }
        alert("Form post edildi");
    };
//class Login extends React.Component {
 //   constructor(props){
  //      super(props);
   //     this.state = {};
   // }

   // render = () => {
    //    const { showRegisterLink } = this.props;
        return (
            <div className="App">
                <Container>
                    <Grid>
                        <Grid.Row columns="equal" centered>
                            <Grid.Column width={8}>
                                Giris Sayfasi
                                <Form 
                                onSubmit = {handleSubmit}
                                onReset ={(e) => {
                                    e.preventDefault();
                                    setUsernamePassword({ username: "", password: ""});
                                }}
                                >
                                    <Form.Field>
                                        <label> Kullanici adi</label>
                                        <Form.Input
                                        type="email"
                                        name="username"
                                        required
                                        error={usernamePasswordError.username}
                                        valeu={usernamePassword.username}
                                        onChange={handleChange}
                                        />
                                    </Form.Field>

                                    <Form.Field>
                                    <label> Sifre</label>
                                        <Form.Input
                                        type="password"
                                        name="password"
                                        required
                                        error={usernamePasswordError.password}
                                        valeu={usernamePassword.password}
                                        onChange={handleChange}
                                        />
                                    </Form.Field>

                                    <Button.Group fluid>
                                        <Button color ="teal" type="reset ">
                                            Sifirla
                                        </Button>
                                        <Button type="submit">Kaydet</Button>
                                    </Button.Group>
                                    </Form>
                                    <Divider />
                                    {showRegisterLink && (
                                        <Button 
                                        type="button"
                                        color="primary"
                                        onClick={() => {
                                            history.push("/");
                                        }}
                                        >Hesabiniz yok mu? Giris yapin.</Button>
                                    )}
                            </Grid.Column>
                        </Grid.Row>
                    </Grid>
                </Container>
            </div>
        );
}

export default Login;
