import { toast } from "react-toastify";
import "./App.css";
import { withRouter } from "react-router-dom";
import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";
import React from "react";
import {
  Button,
  Container,
  Grid,
  Header,
  Table,
  Label,
  Menu,
  Icon,
  Popup,
  Select,
  Input,
  Form,
  Segment,
} from "semantic-ui-react";

class DashboardAdmin1 extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      user: [],
      who: [],
      currentPage: 0,
      bookID: null,
      searchWord: "x",
      bookFav: [],
    };
  };

  componentDidMount = () => {
    this.getUsers();
  };

  statePage = { activeItem: "Kullanıcılar" };

  handleItemClick = (e, { name }) => {
    this.setState({ activeItem: name });
    const { history } = useHistory();
    history.push("/login");
  };

  handleSubmit = (event) => {
    event.preventDefault();
  }
  handleInputChange = (event) => {
    event.preventDefault();
    this.setState({
      searchWord : event.target.value
    })
  }

  deleteAdmin = (favId) => {
    fetch(
      `http://localhost:8081/api/user/deleteFav?id=${favId}`,
      {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
      }
    ).then((r) => {
      if (r.ok) {
        toast.info("Başarıyla silindi");
        return r;
      }
      if (r.status === 401 || r.status === 403 || r.status === 500) {
        return Promise.reject(new Error("Bir hata oluştu"));
      }
    })
    .catch((e) => {
      toast.error(e.message);
    });
  };

  getUsers = () => {
    fetch(
      "http://localhost:8081/api/user/has-role-user",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
      }
    )
      .then((r) => {
        if (r.ok) {
          return r;
        }
        if (r.status === 401 || r.status === 403 || r.status === 500) {
          return Promise.reject(new Error("Bir hata oluştu"));
        }
      })
      .then((r) => {
        return r.json();
      })
      .then((data) => {
        //toast.info(`Toplamda ${data.totalElements} eleman bulundu.`);

        this.setState({ user: data });
      })
      .catch((e) => {
        toast.error(e.message);
      });
  };

  changePageTo = (i) => {
    this.setState({ currentPage: i }, this.getUsers);
  };

  searchuser = () => {
    fetch(
      "http://localhost:8081/api/user/search-user-admin?" +
        new URLSearchParams({ username: this.state.searchWord }),
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
      }
    )
    .then((r) => {
      if (r.ok) {
        return r;
      }
      if (r.status === 401 || r.status === 403 || r.status === 500) {
        return Promise.reject(new Error("Bir hata oluştu"));
      }
    })
    .then((r) => {
      return r.json();
    })
    .then((data) => {
      this.setState({ user: data });
    })
    .catch((e) => {
      toast.error(e.message);
    });
  };

  render = () => {
    const { user } = this.state;
    const { activeItem } = this.statePage;
    return (
      <div className="App">
        <Container>
          <Segment clearing>
            <Header as="h2" content="Device Visibility" floated="left">
              <Grid columns={16}>
                <Grid.Column only="large screen" largeScreen={100}>
                         <p> Hoş Geldiniz </p>
                </Grid.Column>
                <Grid.Column only="mobile" mobile={16}>
                  <Segment>Mobile</Segment>
                </Grid.Column>
              </Grid>
            </Header>

            <Header as="h2" floated="right">
              <Button.Group floated="right">
                <Button color="red">Çıkış Yap</Button>
              </Button.Group>
            </Header>
          </Segment>
          <Grid>
            <Grid.Column stretched width={12}>
              <Segment>
                <Grid.Row columns="equal" centered>
                  <Grid.Column width={16}>
                    <Header as="h2" content="Device Visibility" floated="left">
                      <Grid columns={16}>
                        <Grid.Column
                          only="large screen"
                          largeScreen={100}
                        ></Grid.Column>
                      </Grid>
                    </Header>

                    <Segment clearing> 
                    <Header size="huge" content="Device Visibility" floated = "left">
                      {" "}
                      Kitaplar{" "}
                    </Header>
                    <Header size="huge" content="Device Visibility" floated="right">

                    <Button as={Link} 
                    to= "/search" params={ this.state.searchWord } >    
                    <Icon name = "search" ></Icon>
                    Kitap aramak için tıklayın
                    </Button>
                    </Header>
                    </Segment>
                    <Table celled>
                      <Table.Header>
                        <Table.Row>
                          <Table.HeaderCell></Table.HeaderCell>
                          <Table.HeaderCell>Kullanıcı Adı</Table.HeaderCell>

                          <Table.HeaderCell>Kullanıcıyı Sil</Table.HeaderCell>
                        </Table.Row>
                      </Table.Header>

                      <Table.Body>
                        {user && 

                          user.map((value, index) => (
                            <Table.Row>
                              <Table.Cell>
                                <Label ribbon color = 'yellow'>
                                  {user.number}
                                </Label>
                              </Table.Cell>
                              <Table.Cell>{value.username}</Table.Cell>
                              <Table.Cell>
                                <Popup
                                  trigger={
                                    <Button 
                                      circular
                                      icon = "delete"
                                      color = "red"
                                      onClick = {(e) => {e.preventDefault(); this.deleteAdmin(value.id) } }
                                    />
                                  }
                                  content="Sil"
                                />
                              </Table.Cell>
                            </Table.Row>
                          ))}
                      </Table.Body>
                    </Table>
                  </Grid.Column>
                </Grid.Row>
              </Segment>
            </Grid.Column>

            <Grid.Column width={4}>
              <Menu fluid vertical tabular="right">
                <Menu.Item
                  name="Kitaplar"
                  active={activeItem === "Kitaplar"}
                  as={Link}
                  to="/dashboard-admin-1"
                />
                <Menu.Item
                  name="Kullanıcılar"
                  active={activeItem === "Kullanıcılar"}
                  as={Link}
                  to="/dashboard-admin-2"
                />

              </Menu>
            </Grid.Column>
          </Grid>
        </Container>
      </div>
    );
  };
}

export default withRouter(DashboardAdmin1);
