    render = () => {
        const{book} = this.state;
        return (
            <div className="App">
                <Container>
                    <Grid>
                        <Grid.Row columns="equal" centered>
                            <Grid.Column width={16}>
                                <Header size="huge"> Kitaplar </Header>
                            <Table celled>
                                    <Table.Header>
                                    <Table.Row>
                                        <Table.HeaderCell>Ad</Table.HeaderCell>
                                        <Table.HeaderCell>ISBN</Table.HeaderCell>
                                        <Table.HeaderCell>Yazarlar</Table.HeaderCell>
                                        <Table.HeaderCell>YayÄ±n Evi</Table.HeaderCell>
                                    </Table.Row>
                                    </Table.Header>

                                    <Table.Body>
                                        {book && 
                                        book.map((value,index) => (
                                            <Table.Row>
                                            <Table.Cell>
                                            <Label ribbon>{index+1}</Label>
                                            </Table.Cell>
                                            <Table.Cell>{value.name}</Table.Cell>
                                            <Table.Cell>
                                                <Icon name="barcode" />

                                            </Table.Cell>
                                            <Table.Cell>
                                                {value &&
                                                value.authors && 
                                                value.authors.map((value) => {
                                                    return (
                                                        <>
                                                        <Icon name="pencil alternate" />
                                                        {value.name}
                                                        <br />
                                                        </>
                                                    );
                                                })}
                                            </Table.Cell>
                                        </Table.Row>
                                        ))}
                                    </Table.Body>

                                    <Table.Footer>
                                    <Table.Row>
                                        <Table.HeaderCell colSpan='4'>
                                        <Menu floated='right' pagination>
                                            <Menu.Item as='a' icon>
                                            <Icon name='chevron left' />
                                            </Menu.Item>
                                            <Menu.Item as='a'>1</Menu.Item>
                                            <Menu.Item as='a'>2</Menu.Item>
                                            <Menu.Item as='a'>3</Menu.Item>
                                            <Menu.Item as='a'>4</Menu.Item>
                                            <Menu.Item as='a' icon>
                                            <Icon name='chevron right' />
                                            </Menu.Item>
                                        </Menu>
                                        </Table.HeaderCell>
                                    </Table.Row>
                                    </Table.Footer>
                                </Table>
                            </Grid.Column>
                        </Grid.Row>

                    </Grid>
                </Container>
            </div>
        );
    };
}

export default withRouter(Dashboard);
