import React from 'react';
import { Form, Button, Container, Grid } from "semantic-ui-react";
import './App.css';

class Register extends React.Component {
  constructor(props){
    super(props)
    this.state = {

      username: "",
      password: "",
      passwordRepeat: "",
      counter: 0,
      usernameError : null,
      passwordError : null,
      passwordRepeatError: null,

    };
  }

  handleChange = (e) => {
    const currentTarget = e.currentTarget;
    const {value, name} = currentTarget;
    this.setState( { [name]:value});
  };

  handleSubmit = (e) => {
    e.preventDefault();
    this.setState( { counter: this.state.counter+1} );
    const { username, password, passwordRepeat } = this.state;
    if ( username.length < 5 || username.length > 255 ){
      this.setState( { 
        usernameError: "Lutfen kullanici adinizi kontrol edin",
      });
      return;
    }

    if ( password.length < 3 || username.length > 255 ){
      this.setState( { 
        passwordError: "Lutfen sifrenizi kontrol edin",
      });
      return;
    }

    if ( password !== passwordRepeat){
      this.setState( { 
        passwordError: "Lutfen sifrelerinizi kontrol edin",
        passwordRepeatError: "Lutfen sifrelerinizi kontrol edin",
      });
      return;
    }

  }

  render = () => {
    const { counter, usernameError, passwordError, passwordRepeatError } = this.state;
    return (
      <div className="App">
        <header className="App-header">
          <Container> 
            <Grid>
              <Grid.Row columns="equal" centered>
                <Grid.Column width={8}>
                  <p>
                    Su anki counter degerim: {counter} </p>
                  <Form onSubmit={(e) => {
                    e.preventDefault(); 
                    this.setState({counter:this.state.counter + 1});
                  }}

                  onReset={ (e) => {
                    e.preventDefault();
                    this.setState({
                      counter:0,
                      username: "",
                      password: "",
                      passwordRepeat: "",
                    });
                  }}
                  >

                  <Form.Field>
                  <label> Kullanici adi:  </label>
                  <input 
                    type="email" 
                    name="username" 
                    required ="true" 
                    error = {usernameError}
                    value={this.state.username}
                  onChange={(e) => {this.setState({ username: e.currentTarget.value });
                  }}>
                  </input>
                  </Form.Field>

                  <Form.Field> 
                  <label> Sifre:</label>
                  <input 
                    type="password" 
                    name="password"
                    required ="true"
                    error = {passwordError}
                    value={this.state.password}
                    onChange={this.handleChange}
                    ></input>

                  </Form.Field>
                  <label> Sifre Tekrari:</label>
                  <input 
                    type="password"
                    name="passwordRepeat" 
                    required ="true"
                    error = {passwordRepeatError}
                    value={this.state.passwordRepeat}
                    onChange={this.handleChange}
                  ></input>

                  <Button.Group fluid>
                    <Button color="teal" type="reset">
                      Sifirla
                    </Button>
                  <Button type="submit"> Kaydet </Button>
                  </Button.Group>


                  </Form>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Container>
        </header>
      </div>
    );
  };
}

export default Register;
