import React from 'react';
import { Form, Button, Container, Grid } from "semantic-ui-react";
import './App.css';

class Register extends React.Component {
  constructor(props){
    super(props)
    this.state = {

      username: "",
      password: "",
      passwordRepeat: "",
      counter: 0,

    };
  }

  handleChange = (e) => {
    const currentTarget = e.currentTarget;
    const {value, name} = currentTarget;
    this.setState( { [name]:value});
  };

  render = () => {
    const { counter } = this.state;
    return (
      <div className="App">
        <header className="App-header">
          <Container> 
            <Grid>
              <Grid.Row columns="equal" centered>
                <Grid.Column width={8}>
                  <p>
                    Su anki counter degerim: {counter} </p>
                  <Form onSubmit={(e) => {
                    e.preventDefault(); 
                    this.setState({counter:this.state.counter + 1});
                  }}

                  onReset={ (e) => {
                    e.preventDefault();
                    this.setState({
                      counter:0,
                      username: "",
                      password: "",
                      passwordRepeat: "",
                    });
                  }}
                  >

                  <Form.Field>
                  <label> Kullanici adi:  </label>
                  <input type="email" name="username" required ="true" value={this.state.username}
                  onChange={(e) => {this.setState({ username: e.currentTarget.value });
                  }}>
                  </input>
                  </Form.Field>

                  <Form.Field> 
                  <label> Sifre:</label>
                  <input 
                    type="password" 
                    name="password"
                    required ="true"
                    value={this.state.password}
                    onChange={this.handleChange}
                    ></input>

                  </Form.Field>
                  <label> Sifre Tekrari:</label>
                  <input 
                    type="password"
                    name="passwordRepeat" 
                    required ="true"
                    value={this.state.passwordRepeat}
                    onChange={this.handleChange}
                  ></input>

                  <Button.Group fluid>
                    <Button color="teal" type="reset">
                      Sifirla
                    </Button>
                  <Button type="submit"> Kaydet </Button>
                  </Button.Group>


                  </Form>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Container>
        </header>
      </div>
    );
  };
}

export default Register;
