import {toast} from "react-toastify";
import "./App.css";
import { withRouter } from "react-router-dom";
import React from "react";
import { Container, Grid, Header, Table, Label, Menu, Icon } from "semantic-ui-react";

class Dashboard extends React.Component {
    constructor(props){
        super(props);
        this.state = {};
    }

    componentDidMount = () => {
        this.getBooks();
    }

    getBooks = () => {
        fetch("http://localhost:8081/api/book?" + new URLSearchParams({pageNumber: this.state.currentPage}),
         {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
            },
            credentials: "include",
        })
        .then((r) => {
            if(r.ok){
                return r;
            }
            if( r.status===401 || r.status === 403 || r.status === 500 ){
                return Promise.reject(new Error("Bir hata olustu"));
            }
        })
        .then((r) => {
            return r.json();
        })
        .then((data) => {
            toast.info(`Toplamda ${data.totalElements} eleman bulundu.`);

            this.setState({book:data});
        })
        .catch((e) => {
            toast.error("e.message");
        });
    };

    changePageTo = (i) => {
        this.setState( {currentPage:i}, this.getBooks);
    };

    renderAuthors = (book) => {
