import { toast } from "react-toastify";
import "./App.css";
import { withRouter } from "react-router-dom";
import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";
import React from "react";
import {
  Button,
  Container,
  Grid,
  Header,
  Table,
  Label,
  Menu,
  Icon,
  Popup,
  Input,
  Form,
  Segment,
} from "semantic-ui-react";

class SearchBook extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bookName: "",
      bookAuthor: "",
    };
  };

  componentDidMount = () => {
  //this.searchBook();
  };
  addBook = () => {
    fetch(
      "http://localhost:8081/api/book/addBook" +
        new URLSearchParams({ bookname: this.state.bookName, authorname: this.state.bookAuthor }),
      {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
      }
    )
    .then((r) => {
      if (r.ok) {
        return r;
      }
      if (r.status === 401 || r.status === 403 || r.status === 500) {
        return Promise.reject(new Error("Bir hata oluştu"));
      }
    })
    .then((r) => {
      return r.json();
    })
    .catch((e) => {
      toast.error(e.message);
    });
  };

  statePage = { activeItem: "Kitaplar" };
  handleItemClick = (e, { name }) => {
    this.setState({ activeItem: name });
    const { history } = useHistory();
    history.push("/login");
  };

  handleSubmit = (event) => {
    event.preventDefault();
  }
  handleInputChangeName = (event) => {
    event.preventDefault();
    this.setState({
      bookName : event.target.value
    })
  }
  handleInputChangeName = (event) => {
    event.preventDefault();
    this.setState({
      bookAuthor : event.target.value
    })
  }

  render = () => {
    const { bookName } = this.state;
    const { bookAuthor } = this.state;
    const { activeItem } = this.statePage;

    return (
      <div className="App">
        <Container>
          <Segment clearing>
            <Header as="h2" content="Device Visibility" floated="left">
              <Grid columns={16}>
                <Grid.Column only="large screen" largeScreen={100}>
                         <p> Hoş Geldiniz </p>
                </Grid.Column>
                <Grid.Column only="mobile" mobile={16}>
                  <Segment>Mobile</Segment>
                </Grid.Column>
              </Grid>
            </Header>

            <Header as="h2" floated="right">
              <Button.Group floated="right">
              <Button color="red"

onClick = {(e) => {e.preventDefault(); this.logout() } }

>Çıkış Yap</Button>
              </Button.Group>
            </Header>
          </Segment>
          <Grid>
            <Grid.Column stretched width={12}>
              <Segment>
                <Grid.Row columns="equal" centered>
                  <Grid.Column width={16}>
                    <Header as="h2" content="Device Visibility" floated="left">
                      <Grid columns={16}>
                        <Grid.Column
                          only="large screen"
                          largeScreen={100}
                        ></Grid.Column>
                      </Grid>
                    </Header>

                    <Segment clearing> 
                    <Header size="huge" content="Device Visibility" floated = "left">
                      {" "}
                      Kitaplar{" "}
                    </Header>
                    <Header size="huge" content="Device Visibility" floated="right">

                    </Header>
                    </Segment>

                    <Form onSubmit={this.handleSubmit}>
              <Input type='text' name='bookName' placeholder='Kitap ismi' onChange = {this.handleInputChange}/>
              <Input type='text' name='bookAuthor' placeholder='Yazar' onChange = {this.handleInputChange}/>

              <Button color="vk" onClick={this.addBook}>    
                  Ara
              </Button>
              </Form>


                  </Grid.Column>
                </Grid.Row>
              </Segment>
            </Grid.Column>

            <Grid.Column width={4}>
              <Menu fluid vertical tabular="right">
                <Menu.Item
                  name="Kitaplar"
                  active={activeItem === "Kitaplar"}
                  as={Link}
                  to="/dashboard"
                />
                <Menu.Item
                  name="Favoriler"
                  active={activeItem === "Favoriler"}
                  as={Link}
                  to="/dashboard2"
                />
                <Menu.Item
                  name="Okuma Listesi"
                  active={activeItem === "Okuma Listesi"}
                  as={Link}
                  to="/dashboard3"
                />
              </Menu>
            </Grid.Column>

            <Grid.Column width={12}>

            </Grid.Column>
          </Grid>
        </Container>
      </div>
    );
  };
}

export default withRouter(SearchBook);
