import { toast } from "react-toastify";
import "./App.css";
import { withRouter } from "react-router-dom";
import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";
import React, { Component } from "react";
import {
  Button,
  Container,
  Grid,
  Header,
  Table,
  Label,
  Menu,
  Icon,
  Popup,
  //Search,
  Select,
  Input,
  Dropdown,
  Form,
  Segment,
} from "semantic-ui-react";

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      book: {},
      currentPage: 0,
      bookID: 5,
      userID: 10,
    };
  }

  componentDidMount = () => {
    this.getBooks();
  };

  statePage = { activeItem: "Kitaplar" };

  handleItemClick = (e, { name }) => {
    this.setState({ activeItem: name });
    const { history } = useHistory();
    history.push("/login");
  };

  addToFavs = (e) => {
    fetch(
      `http://localhost:8081/api/user/addFav?id=${this.state.bookID}&userId=${this.state.userID}`,
      {
        method: "PUT",
        headers: {
          //"Content-Type":
        },
        credentials: "include",
      }
    ).then((r) => {
      if (r.ok) {
        return r;
      }
      if (r.status === 401 || r.status === 403 || r.status === 500) {
        return Promise.reject(new Error("xxx"));
      }
    });
    this.setState((prevState) => ({ active: true }));
  };

  addToReads = (book) => {
    fetch(
      `http://localhost:8081/api/user/addRead?id=${this.state.bookID}&userId=${this.state.userID}`,
      {
        method: "PUT",
        headers: {
          //"Content-Type":
        },
        credentials: "include",
      }
    ).then((r) => {
      if (r.ok) {
        return r;
      }
      if (r.status === 401 || r.status === 403 || r.status === 500) {
        return Promise.reject(new Error("xxx"));
      }
    });
  };

  getBooks = () => {
    fetch(
      "http://localhost:8081/api/book?" +
        new URLSearchParams({ pageNumber: this.state.currentPage }),
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
      }
    )
      .then((r) => {
        if (r.ok) {
          return r;
        }
        if (r.status === 401 || r.status === 403 || r.status === 500) {
          return Promise.reject(new Error("Bir hata oluştu"));
        }
      })
      .then((r) => {
        return r.json();
      })
      .then((data) => {
        //toast.info(`Toplamda ${data.totalElements} eleman bulundu.`);

        this.setState({ book: data });
      })
      .catch((e) => {
        toast.error(e.message);
      });
  };

  changePageTo = (i) => {
    this.setState({ currentPage: i }, this.getBooks);
  };

  searchBook = (e) => {
    //const { searchedBookName } = e.target.value;
    this.setState({searchWord: e.target.value});
    toast.info(`Searched box ${searchWord}`);
  };

  renderAuthors = (book) => {
    const items = [];
    if (book && book.authors) {
    }
  };

  render = () => {
    const { book } = this.state;
    const { activeItem } = this.statePage;
    const { active } = false;
    const options = [
      { key: 'ad', text: 'Ada göre', value: 'ad' },
      { key: 'yazar', text: 'Yazara göre', value: 'yazar' },
    ]
    return (
      <div className="App">
        <Container>
          <Segment clearing>
            <Header as="h2" content="Device Visibility" floated="left">
              <Grid columns={16}>
                <Grid.Column only="large screen" largeScreen={100}>
                  <p> Hoş Geldiniz X </p>
                </Grid.Column>
                <Grid.Column only="mobile" mobile={16}>
                  <Segment>Mobile</Segment>
                </Grid.Column>
              </Grid>
            </Header>

            <Header as="h2" floated="right">
              <Button.Group floated="right">
                <Button>Ayarlar</Button>
                <Button color="red">Çıkış Yap</Button>
              </Button.Group>
            </Header>
          </Segment>
          <Grid>
            <Grid.Column stretched width={12}>
              <Segment>
                <Grid.Row columns="equal" centered>
                  <Grid.Column width={16}>
                    <Header as="h2" content="Device Visibility" floated="left">
                      <Grid columns={16}>
                        <Grid.Column
                          only="large screen"
                          largeScreen={100}
                        ></Grid.Column>
                      </Grid>
                    </Header>

                    <Header size="huge" content="Device Visibility">
                      {" "}
                      Kitaplar{" "}
                    </Header>
                    <Table celled>
                      <Table.Header>
                        <Table.Row>
                          <Table.HeaderCell></Table.HeaderCell>
                          <Table.HeaderCell>Ad</Table.HeaderCell>
                          <Table.HeaderCell>Yazar</Table.HeaderCell>
                          <Table.HeaderCell>ISBN</Table.HeaderCell>
                          <Table.HeaderCell></Table.HeaderCell>
                          <Table.HeaderCell></Table.HeaderCell>
                        </Table.Row>
                      </Table.Header>

                      <Table.Body>
                        {book &&
                          book.content &&
                          book.content.map((value, index) => (
                            <Table.Row>
                              <Table.Cell>
                                <Label ribbon>
                                  {book.size * book.number + (index + 1)}
                                </Label>
                              </Table.Cell>
                              <Table.Cell>{value.bookname}</Table.Cell>
                              <Table.Cell>{value.author}</Table.Cell>
                              <Table.Cell>
                                <Icon name="barcode" size="large" />
                              </Table.Cell>
                              <Table.Cell>
                                <Popup
                                  trigger={
                                    <Icon
                                      name="heart outline"
                                      color="red"
                                      onClick={this.addToFavs}
                                    />
                                  }
                                  content="Favorilere ekle"
                                />
                              </Table.Cell>
                              <Table.Cell>
                                <Popup
                                  trigger={
                                    <Icon
                                      name="bookmark outline"
                                      color="black"
                                      onClick={this.addToReads}
                                    />
                                  }
                                  content="Okuma listesine ekle"
                                />
                              </Table.Cell>
                            </Table.Row>
                          ))}
                      </Table.Body>

                      <Table.Footer>
                        <Table.Row>
                          <Table.HeaderCell colSpan="6">
                            <Menu floated="right" pagination>
                              <Menu.Item
                                onClick={() => {
                                  this.changePageTo(this.state.currentPage - 1);
                                }}
                                as="a"
                                icon
                                disabled={book.first}
                              >
                                <Icon name="chevron left" />
                              </Menu.Item>
                              {[...Array(book.totalPages).keys()].map(
                                (value, index) => (
                                  <Menu.Item
                                    as="a"
                                    onClick={() => {
                                      this.changePageTo(index);
                                    }}
                                    active={book.number === index}
                                  >
                                    {index + 1}
                                  </Menu.Item>
                                )
                              )}

                              <Menu.Item
                                onClick={() => {
                                  this.changePageTo(this.state.currentPage + 1);
                                }}
                                as="a"
                                icon
                                disabled={book.last}
                              >
                                <Icon name="chevron right" />
                              </Menu.Item>
                            </Menu>
                          </Table.HeaderCell>
                        </Table.Row>
                      </Table.Footer>
                    </Table>
                  </Grid.Column>
                </Grid.Row>
              </Segment>
            </Grid.Column>

            <Grid.Column width={4}>
              <Menu fluid vertical tabular="right">
                <Menu.Item
                  name="Kitaplar"
                  active={activeItem === "Kitaplar"}
                  //onClick={this.handleItemClick}
                  as={Link}
                  to="/dashboard"
                />
                <Menu.Item
                  name="Favoriler"
                  active={activeItem === "Favoriler"}
                  as={Link}
                  to="/dashboard2"
                />
                <Menu.Item
                  name="Okuma Listesi"
                  active={activeItem === "Okuma Listesi"}
                  as={Link}
                  to="/dashboard3"
                />
              </Menu>
            </Grid.Column>

            <Grid.Column width={12}>

              <Segment clearing>

              <Form>
              <Input type='text' input value = {this.state.searchWord} name='searchWord' placeholder='Kitap arayın...' action>
              <Input />
              <Select compact options={options} defaultValue='ad' />
              <Button type='submit' onClick={this.searchBook}>Ara</Button>
              </Input>
                <Header as="h3" floated="right">
                </Header>
              </Form>

              </Segment>
            </Grid.Column>
          </Grid>
        </Container>
      </div>
    );
  };
}

export default withRouter(Dashboard);
