import { toast } from "react-toastify";
import "./App.css";
import { withRouter } from "react-router-dom";
import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";
import React from "react";
import {
  Button,
  Container,
  Grid,
  Header,
  Table,
  Label,
  Menu,
  Icon,
  Popup,
  Responsive,
  Dropdown,
  Segment,
} from "semantic-ui-react";

class Dashboard2 extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      book: [],
      currentPage: 0,
      bookID: 4,
      userID: 10
    };
  }

  componentDidMount = () => {
    this.getFavs();
  };

  statePage = { activeItem: "Favoriler" };

  handleItemClick = (e, { name }) => {
    this.setState({ activeItem: name });
    const {history} = useHistory();
    history.push("/login");
  }

  getFavs = () => {
    fetch(
      "http://localhost:8081/api/user/seeFav?" +
        new URLSearchParams({ id: 10}),
      {
        method: "GET",
        headers: {
         "Content-Type": "application/json",
        },
        credentials: "include",
      }
    )
      .then((r) => {
        if (r.ok) {
          return r;
        }
        if (r.status === 401 || r.status === 403 || r.status === 500) {
          return Promise.reject(new Error("Bir hata oluÅŸtu"));
        }
      })
      .then((r) => {
        return r.json();
      })
      .then((data) => {
        toast.info(`Toplamda ${data.length} eleman bulundu.`);

        this.setState({ book: data });
      })
      .catch((e) => {
        toast.error(e.message);
      });
  };

  changePageTo = (i) => {
    this.setState({ currentPage: i }, this.getBooks);
  };

  render = () => {
    const { book } = this.state;
    const { activeItem } = this.statePage;
    return (
      <div className="App">
        <Container>
          <Segment clearing>

            <Header as="h2" content='Device Visibility' floated="left">
                <Grid columns={16}>
                <Grid.Column only='large screen' largeScreen={100}>
                    <p> HoÅŸ Geldiniz X </p>
                </Grid.Column>
                <Grid.Column only='mobile' mobile={16}>
                <Segment>Mobile</Segment>
                </Grid.Column>
                </Grid>
            </Header>

            <Header as="h2" floated="right">
              <Button.Group floated="right">
                <Button>Ayarlar</Button>
                <Button color="red">Ã‡Ä±kÄ±ÅŸ Yap</Button>
              </Button.Group>
            </Header>
          </Segment>
          <Grid>
            <Grid.Column stretched width={12}>
              <Segment>
                <Grid.Row columns="equal" centered>
                  <Grid.Column width={16}>


                  <Header as="h2" content='Device Visibility' floated="left">
                <Grid columns={16}>
                <Grid.Column only='large screen' largeScreen={100}>

                </Grid.Column>
                </Grid>
            </Header>

                    <Header size="huge" content='Device Visibility'> Favori KitaplarÄ±nÄ±z </Header>
                    <Table celled>
                      <Table.Header>
                        <Table.Row>
                          <Table.HeaderCell></Table.HeaderCell>
                          <Table.HeaderCell>Ad</Table.HeaderCell>
                          <Table.HeaderCell>Yazar</Table.HeaderCell>
                          <Table.HeaderCell>ISBN</Table.HeaderCell>
                        </Table.Row>
                      </Table.Header>

                      <Table.Body>
                        {book &&
                          book.map((value, index) => (
                            <Table.Row>
                              <Table.Cell>
                                <Label ribbon color="green">
                                  {}
                                </Label>
                              </Table.Cell>
                              <Table.Cell>{value.bookname}</Table.Cell>
                              <Table.Cell>{value.author}</Table.Cell>
                              <Table.Cell>
                                <Icon name="barcode" size="large" />
                              </Table.Cell>
                              <Table.Cell>
                              </Table.Cell>
                            </Table.Row>
                          ))}
                      </Table.Body>

                      <Table.Footer>
                        <Table.Row>
                          <Table.HeaderCell colSpan="4">

                          </Table.HeaderCell>
                        </Table.Row>
                      </Table.Footer>
                    </Table>
                  </Grid.Column>
                </Grid.Row>
              </Segment>
            </Grid.Column>

            <Grid.Column width={4}>
              <Menu fluid vertical tabular="right">
                <Menu.Item
                  name="Kitaplar"
                  active={activeItem === "Kitaplar"}
                  as={Link}
                  to="/dashboard1"
                />
                <Menu.Item
                  name="Favoriler"
                  active={activeItem === "Favoriler"}
                />
                <Menu.Item
                  name="Okuma Listesi"
                  active={activeItem === "Okuma Listesi"}
                  as={Link}
                  to="/dashboard3"
                />
              </Menu>
            </Grid.Column>
            </Grid>
        </Container>
      </div>
    );
  };
}

export default withRouter(Dashboard2);
