import { toast } from "react-toastify";
import "./App.css";
import { withRouter } from "react-router-dom";
import { Link } from "react-router-dom";
import { useHistory } from "react-router-dom";
import React from "react";
import {
  Button,
  Container,
  Grid,
  Header,
  Menu,
  Segment,
  Form,
  Input,

} from "semantic-ui-react";

class Settings extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      newPassword: "",
    };
  }

  statePage = { activeItem: "" };

  handleSubmit = (event) => {
    event.preventDefault();
  }
  handleInputChange = (event) => {
    event.preventDefault();
    this.setState({
      newPassword : event.target.value
    })
  }

  changePassword = (newPassword) => {
    fetch(
      `http://localhost:8081/api/user/addFav?id=${newPassword}`,
      {
        method: "PUT",
        headers: {
          //"Content-Type": "application/json",
        },
        credentials: "include",
      }
    ).then((r) => {
      if (r.ok) {
        toast.info("Favorilerinize eklendi.");
        return r;
      }
      if (r.status === 401 || r.status === 403 || r.status === 500) {
        return Promise.reject(new Error("Bir hata oluştu"));
      }
    })
    .catch((e) => {
      toast.error(e.message);
    });
  };

  render = () => {
    const { activeItem } = this.statePage;
    return (
      <div className="App">
        <Container>
          <Segment clearing>

            <Header as="h2" content='Device Visibility' floated="left">
                <Grid columns={16}>
                <Grid.Column only='large screen' largeScreen={100}>
                    <p> Hoş Geldiniz</p>
                </Grid.Column>
                </Grid>
            </Header>

            <Header as="h2" floated="right">
              <Button.Group floated="right">
                <Button>Ayarlar</Button>
                <Button color="red">Çıkış Yap</Button>
              </Button.Group>
            </Header>
          </Segment>
          <Grid>
            <Grid.Column stretched width={12}>
              <Segment>
                <Grid.Row columns="equal" centered>
                  <Grid.Column width={16}>


                  <Header as="h2" content='Device Visibility' floated="left">
                <Grid columns={16}>
                <Grid.Column only='large screen' largeScreen={100}>

                </Grid.Column>
                </Grid>
            </Header>
                    <Form onSubmit={this.handleSubmit}>
                    <Input 
                      type='password'
                      name='password' 
                      placeholder='Yeni şifre girin'
                      onChange = {this.handleInputChange}/>
                    <Button 
                      color="vk" 
                      onClick={this.changePassword}>    
                      Değiştir
                    </Button>
              </Form>
                  </Grid.Column>
                </Grid.Row>
              </Segment>
            </Grid.Column>

            <Grid.Column width={4}>
              <Menu fluid vertical tabular="right">
                <Menu.Item
                  name="Kitaplar"
                  as={Link}
                  to="/dashboard"
                />
                <Menu.Item
                  name="Favoriler" 
                  as={Link}
                  to="/dashboard2"
                />
                <Menu.Item
                  name="Okuma Listesi"
                  as={Link}
                  to="/dashboard3"
                />
              </Menu>
            </Grid.Column>
            </Grid>
        </Container>
      </div>
    );
  };
}

export default withRouter(Settings);
