import { toast } from "react-toastify";
import "./App.css";
import { withRouter } from "react-router-dom";
import React from "react";
import {
  Button,
  Container,
  Grid,
  Header,
  Table,
  Label,
  Menu,
  Icon,
  Popup,
  Responsive,
  Segment,
} from "semantic-ui-react";

class Dashboard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      book: {},
      currentPage: 0,
    };
  }

  componentDidMount = () => {
    this.getBooks();
  };

  statePage = { activeItem: "Kitaplar" };

  handleItemClick = (e, { name }) => this.setState({ activeItem: name });

  getBooks = () => {
    fetch(
      "http://localhost:8081/api/book?" +
        new URLSearchParams({ pageNumber: this.state.currentPage }),
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
      }
    )
      .then((r) => {
        if (r.ok) {
          return r;
        }
        if (r.status === 401 || r.status === 403 || r.status === 500) {
          return Promise.reject(new Error("Bir hata oluştu"));
        }
      })
      .then((r) => {
        return r.json();
      })
      .then((data) => {
        //toast.info(`Toplamda ${data.totalElements} eleman bulundu.`);

        this.setState({ book: data });
      })
      .catch((e) => {
        toast.error("e.message");
      });
  };

  changePageTo = (i) => {
    this.setState({ currentPage: i }, this.getBooks);
  };

  renderAuthors = (book) => {
    const items = [];
    if (book && book.authors) {
    }
  };

  render = () => {
    const { book } = this.state;
    const { activeItem } = this.statePage;
    return (
      <div className="App">
        <Container>
          <Segment clearing>
            <Header as="h2" floated="left">
              Hoş Geldiniz X
            </Header>
            <Header as="h2" floated="right">
              <Button.Group floated="right">
                <Button>Ayarlar</Button>
                <Button color="red">Çıkış Yap</Button>
              </Button.Group>
            </Header>
          </Segment>
          <Grid>
            <Grid.Column stretched width={12}>
              <Segment>
                <Grid.Row columns="equal" centered>
                  <Grid.Column width={16}>
                    <Header size="huge"> Kitaplar </Header>
                    <Table celled>
                      <Table.Header>
                        <Table.Row>
                          <Table.HeaderCell></Table.HeaderCell>
                          <Table.HeaderCell>Ad</Table.HeaderCell>
                          <Table.HeaderCell>Yazar</Table.HeaderCell>
                          <Table.HeaderCell>ISBN</Table.HeaderCell>
                          <Table.HeaderCell></Table.HeaderCell>
                          <Table.HeaderCell></Table.HeaderCell>
                        </Table.Row>
                      </Table.Header>

                      <Table.Body>
                        {book &&
                          book.content &&
                          book.content.map((value, index) => (
                            <Table.Row>
                              <Table.Cell>
                                <Label ribbon>
                                  {book.size * book.number + (index + 1)}
                                </Label>
                              </Table.Cell>
                              <Table.Cell>{value.bookname}</Table.Cell>
                              <Table.Cell>{value.author}</Table.Cell>
                              <Table.Cell>
                                <Icon name="barcode" size="large" />
                              </Table.Cell>
                              <Table.Cell>
                                <Popup
                                  trigger={
                                    <Icon name="heart outline" color="black" />
                                  }
                                  content="Favorilere ekle"
                                  position="top center"
                                />
                              </Table.Cell>
                              <Table.Cell>
                                <Popup
                                  trigger={
                                    <Icon
                                      name="bookmark outline"
                                      color="black"
                                    />
                                  }
                                  content="Okuma listesine ekle"
                                  //style={style}
                                  ///inverted
                                />
                              </Table.Cell>
                            </Table.Row>
                          ))}
                      </Table.Body>

                      <Table.Footer>
                        <Table.Row>
                          <Table.HeaderCell colSpan="6">
                            <Menu floated="right" pagination>
                              <Menu.Item
                                onClick={() => {
                                  this.changePageTo(this.state.currentPage - 1);
                                }}
                                as="a"
                                icon
                                disabled={book.first}
                              >
                                <Icon name="chevron left" />
                              </Menu.Item>
                              {[...Array(book.totalPages).keys()].map(
                                (value, index) => (
                                  <Menu.Item
                                    as="a"
                                    onClick={() => {
                                      this.changePageTo(index);
                                    }}
                                    active={book.number === index}
                                  >
                                    {index + 1}
                                  </Menu.Item>
                                )
                              )}

                              <Menu.Item
                                onClick={() => {
                                  this.changePageTo(this.state.currentPage + 1);
                                }}
                                as="a"
                                icon
                                disabled={book.last}
                              >
                                <Icon name="chevron right" />
                              </Menu.Item>
                            </Menu>
                          </Table.HeaderCell>
                        </Table.Row>
                      </Table.Footer>
                    </Table>
                  </Grid.Column>
                </Grid.Row>
              </Segment>
            </Grid.Column>

            <Grid.Column width={4}>
              <Menu fluid vertical tabular="right">
                <Menu.Item
                  name="Kitaplar"
                  active={activeItem === "Kitaplar"}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  name="Favoriler"
                  active={activeItem === "Favoriler"}
                  onClick={this.handleItemClick}
                />
                <Menu.Item
                  name="Okuma Listesi"
                  active={activeItem === "Okuma Listesi"}
                  onClick={this.handleItemClick}
                />
              </Menu>
            </Grid.Column>
          </Grid>

          <Segment clearing>
            <Footer floated="left">
              Hoş Geldiniz X
            </Footer>
            <Footer as="h2" floated="right">
              <Button.Group floated="right">
                <Button>Ayarlar</Button>
                <Button color="red">Çıkış Yap</Button>
              </Button.Group>
            </Footer>
          </Segment>
        </Container>
      </div>
    );
  };
}

export default withRouter(Dashboard);
