import React from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

import Register from "./Register";
import Login from "./Login";

export default function App(){
    return (
        <Router>
            <div>
                <Switch>
                    <Route exact path="/">
                        <Register />
                    </Route>
                    <Route path="/login">
                        <Login showRegisterLink/>
                    </Route>
                </Switch>
            </div>
        </Router>
    );
}
