import React from "react";
import { Container, Grid, Divider } from "semantic-ui-react";
import { Link } from "react-router-dom";
import "./App.css";

class Login extends React.Component {
    constructor(props){
        super(props);
        this.state = {};
    }

    render = () => {
        const { showRegisterLink } = this.props;
        return (
            <div className="App">
                <Container>
                    <Grid>
                        <Grid.Row columns="equal" centered>
                            <Grid.Column width={8}>
                                Giris Sayfasi
                                <Divider />
                                {showRegisterLink && (
                                    <Link to="/"> Hesabiniz yok mu? Kayit olun</Link>
                                )}
                            </Grid.Column>
                        </Grid.Row>
                    </Grid>
                </Container>
            </div>
        );
    };
}
