import React from 'react';
import { Form, Button, Container, Grid } from "semantic-ui-react";
import './App.css';

class Register extends React.Component {
  constructor(props){
    super(props)
    this.state = {

      username: "",
      password: "",
      passwordRepeat: "",
      counter: 0,

    };
  }

  handleChange = (e) => {
    const currentTarget = e.currentTarget;
    const {value, name} = currentTarget;
    this.setState( { [name]:value});
  };

  render = () => {
    return (
      <div className="App">
        <header className="App-header">
          <Container> 
            <Grid>
              <Grid.Row columns="equal" centered>
                <Grid.Column width={8}>
                  <p>
                    Ilk react uygulamasi
                  </p>
                  <Form onSubmit={(e) => {e.preventDefault(); alert("ilk formumu postladim");}}>

                  <Form.Field>
                  <label> Kullanici adi:  </label>
                  <input type="email" name="username" required ="true" value={this.state.username}
                  onChange={(e) => {this.setState({ username: e.currentTarget.value });
                  }}>
                  </input>
                  </Form.Field>

                  <Form.Field> 
                  <label> Sifre:</label>
                  <input type="password" required ="true"></input>

                  </Form.Field>
                  <label> Sifre Tekrari:</label>
                  <input type="password" required ="true"></input>
                  <Form.Field> </Form.Field>

                  <Button type="submit"> Kaydet </Button>
                  </Form>
                </Grid.Column>
              </Grid.Row>
            </Grid>
          </Container>
        </header>
      </div>
    );
  };
}

export default Register;
