    }

    };

    render = () => {
        const{book} = this.state;
        return (
            <div className="App">
                <Container>
                <Responsive as={Segment} minWidth={992}>
                 Visible only if display has <code>992px</code> width and higher
                 </Responsive>
                    <Grid>
                        <Grid.Row columns="equal" centered>
                            <Grid.Column width={16}>
                                <Header size="huge"> Kitaplar </Header>
                            <Table celled>
                                    <Table.Header>
                                    <Table.Row>
                                        <Table.HeaderCell></Table.HeaderCell>
                                        <Table.HeaderCell>Ad</Table.HeaderCell>
                                        <Table.HeaderCell>Yazar</Table.HeaderCell>
                                        <Table.HeaderCell>ISBN</Table.HeaderCell>
                                        <Table.HeaderCell></Table.HeaderCell>
                                        <Table.HeaderCell></Table.HeaderCell>
                                    </Table.Row>
                                    </Table.Header>

                                    <Table.Body>
                                        {book && 
                                        book.content &&
                                        book.content.map((value,index) => (
                                            <Table.Row>
                                            <Table.Cell>
                                            <Label ribbon>{book.size * book.number + (index+1)}</Label>
                                            </Table.Cell>
                                            <Table.Cell>{value.bookname}</Table.Cell>
                                            <Table.Cell>{value.author}</Table.Cell>
                                            <Table.Cell>
                                                <Icon name="barcode" size="large"/>   
                                            </Table.Cell>
                                            <Table.Cell>
                                            <Popup
                                                trigger={<Icon name='heart outline' color='black' />}
                                                content='Favorilere ekle'
                                                position='top center'
                                            /> 
                                            </Table.Cell>
                                            <Table.Cell>
                                            <Popup
                                                trigger={<Icon name='bookmark outline' color='black' />}
                                                content='Okuma listesine ekle'
                                                //style={style}
                                                ///inverted
                                            />
                                            </Table.Cell>
                                        </Table.Row>
                                        ))}
                                    </Table.Body>

                                    <Table.Footer>

                                    <Table.Row>
                                        <Table.HeaderCell colSpan='6'>
                                        <Menu floated='right' pagination>
                                            <Menu.Item 
                                                onClick={ () => {
                                                    this.changePageTo(this.state.currentPage - 1);
                                                } }
                                                as="a"
                                                icon
                                                disabled={book.first}
                                            >
                                            <Icon name='chevron left' />
                                            </Menu.Item>
                                            {[...Array(book.totalPages).keys()].map(
                                                (value,index) => ( 
                                                    <Menu.Item 
                                                        as='a' 
                                                        onClick={() => {
                                                            this.changePageTo(index);
                                                        }}
                                                        active={book.number === index}
                                                    >
                                                        {index+1}
                                                    </Menu.Item>
                                                )
                                            )}

                                            <Menu.Item 
                                                onClick={() => {
                                                    this.changePageTo(this.state.currentPage + 1);
                                                }}
                                                as='a'
                                                icon
                                                disabled={book.last}
                                            >
                                            <Icon name='chevron right' />
                                            </Menu.Item>
                                        </Menu>
                                        </Table.HeaderCell>
                                    </Table.Row>
                                    </Table.Footer>
                                </Table>
                            </Grid.Column>
                        </Grid.Row>

                    </Grid>
                </Container>
            </div>
        );
    };
}

export default withRouter(Dashboard);
